import java.util.ArrayList;import java.util.Collection;import com.ibsplc.icargo.business.mail.mra.airlinebilling.defaults.vo.UPUCalendarFilterVO;import com.ibsplc.icargo.business.mail.mra.airlinebilling.defaults.vo.UPUCalendarVO;import com.ibsplc.icargo.framework.session.AbstractScreenSession;import com.ibsplc.icargo.presentation.web.session.interfaces.mail.mra.airlinebilling.defaults.UPUCalendarSession;/** *  * @author A-2521 * */public class UPUCalendarSessionImpl extends AbstractScreenSession implements																	UPUCalendarSession{		/*	 * The module name	 */	private static final String MODULE_NAME = "mailtracking.mra";	/*	 * The screen id	 */	private static final String SCREEN_ID =	"mailtracking.mra.airlinebilling.defaults.upucalendar";	private static final String KEY_UPUCALENDARVOS = "upuCalendarVo";			private static final String KEY_UPUCALENDARFILTERVO = "upuCalendarFilterVO";	/**    * returns screen id    *     * @return SCREEN_ID    */    public String getScreenID() {        return SCREEN_ID;    }   /**    * returns module name    *     * @return MODULE_NAME    */    public String getModuleName() {        return MODULE_NAME;    }    /**	 * This method returns the UPUCalendarVO in session	 * @return Collection<UPUCalendarVO>	 */	public Collection<UPUCalendarVO> getUPUCalendarVOs(){		return (Collection<UPUCalendarVO>) getAttribute(KEY_UPUCALENDARVOS);	}	/**	 * This method sets the UPUCalendarVO in session	 * @param  upuCalendarVO	 */	public void setUPUCalendarVOs(Collection<UPUCalendarVO> upuCalendarVO){		setAttribute(KEY_UPUCALENDARVOS,(ArrayList<UPUCalendarVO>) upuCalendarVO);	}	/**	 * This method removes the UPUCalendarVO in session	 */	public void removeUPUCalendarVOs(){		removeAttribute(KEY_UPUCALENDARVOS);	}		/**	 * Getting the UPUCalendarFilterVO 	 * @return UPUCalendarFilterVO	 */	 	public UPUCalendarFilterVO getUPUCalendarFilterVO() {		return getAttribute(KEY_UPUCALENDARFILTERVO);	}		/**	 * Setting upuCalendarFilterVO. 	 * 	 * @param upuCalendarFilterVO	 */	public void setUPUCalendarFilterVO(UPUCalendarFilterVO upuCalendarFilterVO) {		setAttribute(KEY_UPUCALENDARFILTERVO, upuCalendarFilterVO);	}	/**	 * removes upuCalendarFilterVO	 */	public void removeUPUCalendarFilterVO() {		removeAttribute(KEY_UPUCALENDARFILTERVO);	}	}